<?PHP	#********************************************	# création et postage des données du collège	#	# création de la salle D3E et STOCK	#	#********************************************			// lib	include_once ('config/databases.php');	// fichiers de configuration des bases de données	require_once ('fonctions.php');	include_once ('../class/Sql.class.php');	?><div class="entetes" id="entete-college">		<span class="entetes-titre">CREATION DU COLLEGE<img class="help-button" src="img/icons/info.png"></span>	<div class="helpbox">C'est la fiche de création du collège et d'initialisation de la base (création des salles spéciales ...)</div></div><div class="spacer"></div> <script>	window.addEvent('domready', function(){			$('menu').hide();		$('post_form').addEvent('submit', function(e) {	//	Pour poster un formulaire			new Event(e).stop();			new Request({				method: this.method,				url: this.action,				onSuccess: function(responseText, responseXML) {					$('targetback').setStyle("display","block"); $('target').setStyle("display","block");					$('target').set('html', responseText);					window.setTimeout("document.location.href='index.php?page=college'", 2500);				}			}).send(this.toQueryString());		});		});		// Validation du formulaire	function validation () {		var bt_submit = $("post_college");		var clg_uai   = $("clg_uai").value;		var clg_nom   = $("clg_nom").value;		var clg_mail  = $("clg_ati_mail").value;		var clg_ville = $("clg_ville").value;		if (clg_uai == "" || clg_nom == "" || clg_mail == "" || clg_ville == "") {			bt_submit.disabled = true;		} else {			bt_submit.disabled = false;		}	}	// Donne le focus au premier champ du formulaire	$('clg_uai').focus();</script><center>		<h3>ATTENTION : Merci de ne PAS utiliser l'adresse @CG13 pour compléter le mail ATI !</h3><hr>	<form action="gestion_college/post_college.php?action=creat" method="post" name="post_form" id="post_form">				<center>				<table width=500>					<tr>						<TD>UAI *</TD>						<TD align=left><input type=text id=clg_uai name=clg_uai size=8 maxlength=8 onkeyup="validation();"/ required></TD>					</tr>									<tr>						<TD>Nom collège *</TD>						<TD align=left><input type=text id=clg_nom name=clg_nom size=30  onkeyup="validation();"/ required></TD>					</tr>									<tr>						<TD>Nom et prénom ATI</TD>						<TD align=left><input type=text id=clg_ati name=clg_ati /></TD>					</tr>													<tr>						<TD>Mail ATI *</TD>						<TD align=left><input type=mail id=clg_ati_mail name=clg_ati_mail onkeyup="validation();"/ required></TD>					</tr>									<tr>						<TD>Adresse</TD>						<TD align=left><input type=text id=clg_adresse name=clg_adresse maxlength=40 /></TD>					</tr>									<tr>						<TD>Code Postal</TD>						<TD align=left><input type=text name=clg_cp size=5 maxlength=5 /></TD>					</tr>									<tr>						<TD>Ville *</TD>						<TD align=left><input type=text name=clg_ville id=clg_ville onkeyup="validation();" / required></TD>					</tr>										<tr>						<TD>Tel</TD>						<TD align=left><input type=text name=clg_tel /></TD>					</tr>									<tr>						<TD>Fax</TD>						<TD align=left><input type=text name=clg_fax /></TD>					</tr>									<tr>						<TD>Site web</TD>						<TD align=left>http://<input type=text size=40 name=clg_web /></TD>					</tr>													<tr>						<TD>Accès GRR</TD>						<TD align=left>http://<input type=text size=40 name=clg_grr /></TD>					</tr>										</table>					<br>		<center>			<input type=submit name=Envoyer value="Créer le collège" id="post_college" disabled onclick="$('main_menu').style.display = '';"/><br><br>					</center>	</form></center>