<?PHP	#********************************************	#	# modification des données du collège	#	#********************************************			// lib	include_once ('../config/databases.php');	// fichiers de configuration des bases de données	require_once ('../fonctions.php');	include_once ('../../class/Sql.class.php');	?> <script>		// **************************************************************** POST AJAX FORMULAIRES		$("#post_form").click(function(event) {			/* stop form from submitting normally */			event.preventDefault(); 			if ( validForm() == true) {				// Permet d'avoir les données à envoyer				var dataString = $("#formulaire").serialize();								// action du formulaire				var url = $("#formulaire").attr( 'action' );								var request = $.ajax({					type: "POST",					url: url,					data: dataString,					dataType: "html"				 });				 				 request.done(function(msg) {					$('#dialog').dialog('close');					$('#targetback').show(); $('#target').show();					$('#target').html(msg);					window.setTimeout("document.location.href='index.php?page=college'", 2500);				 });			}	 		});				 	// Donne le focus au premier champ du formulaire	$('#clg_uai').focus();</script><?PHP		$clg_uai = $_GET['id'];		if ( $clg_uai ) {			#***************************************************************************#		# 		Requete pour récupérer les données des champs du collège			#		#***************************************************************************#				// cnx à la base de données GESPAC		$con_gespac 	= new Sql ( $host, $user, $pass, $gespac );		// stockage des lignes retournées par sql dans un tableau nommé avec originalité "array" (mais "tableau" peut aussi marcher)		$college_a_modifier = $con_gespac->QueryRow ( "SELECT * FROM college WHERE clg_uai='$clg_uai'" );				$clg_uai 		= $college_a_modifier[0];		$clg_nom 		= $college_a_modifier[1];		$clg_ati 		= $college_a_modifier[2];		$clg_ati_mail 	= $college_a_modifier[3];		$clg_adresse 	= $college_a_modifier[4];		$clg_cp 		= $college_a_modifier[5];		$clg_ville 		= $college_a_modifier[6];		$clg_tel 		= $college_a_modifier[7];		$clg_fax 		= $college_a_modifier[8];		$clg_web 		= $college_a_modifier[9];		$clg_grr 		= $college_a_modifier[10];?><form name="post_form" id="formulaire" action="gestion_college/post_college.php?action=mod" method="post">	<center>	<input type=hidden name=old_uai value=<?PHP echo $clg_uai ?> />		<table class='formtable'>		<tr>			<TD>UAI *</TD>			<TD align=left><input type=text name=clg_uai id=clg_uai size=10 maxlength=8 value= "<?PHP echo $clg_uai; ?>" class="valid uai nonvide" required></TD>		</tr>				<tr>			<TD>Nom collège *</TD>			<TD align=left><input type=text name=clg_nom id=clg_nom size=30 value= "<?PHP echo $clg_nom; ?>" class="valid nonvide" required></TD>		</tr>			<tr>			<TD>Nom et prénom ATI *</TD>			<TD align=left><input type=text name=clg_ati size=50 value= "<?PHP echo $clg_ati; ?>" class="valid nonvide" required></TD>		</tr>							<tr>			<TD>Mail ATI *</TD>			<TD align=left><input type=text size=50 name=clg_ati_mail id=clg_ati_mail value="<?PHP echo $clg_ati_mail; ?>" class="valid nonvide mailnoncg13"></TD>		</tr>			<tr>			<TD>Adresse</TD>			<TD align=left><input type=text name=clg_adresse size=50 value= "<?PHP echo $clg_adresse; ?>"></TD>		</tr>			<tr>			<TD>Code Postal</TD>			<TD align=left><input type=text name=clg_cp size=5 maxlength=5 value= "<?PHP echo $clg_cp; ?>"></TD>		</tr>			<tr>			<TD>Ville *</TD>			<TD align=left><input type=text name=clg_ville id=clg_ville required value= "<?PHP echo $clg_ville; ?>" class="valid nonvide"></TD>		</tr>				<tr>			<TD>Tel</TD>			<TD align=left><input type=text name=clg_tel size=12 maxlength=10 value= "<?PHP echo $clg_tel; ?>"></TD>		</tr>			<tr>			<TD>Fax</TD>			<TD align=left><input type=text name=clg_fax size=12 maxlength=10 value= "<?PHP echo $clg_fax; ?>"></TD>		</tr>			<tr>			<TD>